class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> v(32);
        int w=0,ans=INT_MAX;
        for(int i=0;i<nums.size();i++)
        {
            int r=nums[i],n=0;
            for(int j=0;j<32;j++)
            {
                if(r>>j &1)
                    v[j]++;
            }
            for(int j=0;j<32;j++)
            {
                if(v[j]==i-w+1)
                {
                    n+=(1<<j);
                }
            }
            ans=min(ans,abs(k-n));
            while(n<k)
            {
                if(w==i)
                    break;
                int z=nums[w++];
                for(int j=0;j<32;j++)
            {
                if(z>>j &1)
                    v[j]--;
            }
                n=0;
                for(int j=0;j<32;j++)
            {
                if(v[j]==i-w+1)
                {
                    n+=(1<<j);
                }
            }
                ans=min(ans,abs(k-n));
            }
        }
        return ans;
    }
};
