class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector <vector <int>> dp(n, vector <int> (32, 0));

        for(int i=0;i<n;i++){
            int idx =0;
            int num = nums[i];
            while(num>0){
                int rem = num%2;
                dp[i][idx] = rem;
                idx++;
                num/=2;
            }
        }

