        for(int i=0;i<32;i++){
            int j=0;
            // cout<<i<<" -> ";
            while(j<n){
                int temp = nums[j];
                if (dp[j][i]==1){
                    while(j<n && dp[j][i]==1){

                       temp=temp&nums[j];
                        ans=min(ans, abs(k-temp));
                    j++;
                }
                }
                else{

                    j++;
                }

                // cout<<temp<<" ";
                ans=min(ans, abs(k-temp));
                // j++;
            }
            // cout<<endl;


        }
        for(int i=0;i<32;i++){
            int j=0;
            // cout<<i<<" -> ";
