class Solution {
public:
    // int use=((int)1<<30)-1;

    class SGtree{
public:
    vector<int> seg;
public:

    SGtree(int n){
        seg.resize(4*n+1);
    }
    void combine(int id){
        seg[id]=((int)seg[2*id]&seg[(2*id)+1]);
        return;
    }
    void build(int id,int l,int r,vector<int> &a){
        if(r-l==1){

            seg[id]=a[l];
            return;
        }

        int mid=((int)l+r)/2;

        build(2*id,l,mid,a);
        build(2*id+1,mid,r,a);


        combine(id);

        return;
    }

    int query(int id,int x,int y,int l,int r){
     if(l>=x and r<=y){

        return seg[id];
    }
    else if(l>=y or r<=x){


        int use=((int)1<<30)-1;
        return use;
    }

    int mid=((int)l+r)/2;


    int left=query(2*id,x,y,l,mid);
    int right=query(2*id+1,x,y,mid,r);
    return ((int)left&right);
}

void update(int id,int l,int r,int index,int value){

   if(r<=index or l>index){
    return;
}
if((r-l)==1){
    if(index==l){
        seg[id]=value;
    }
    return;

}

int mid=(int)l+r;
mid=mid/2;
update(2*id,l,mid,index,value);
update((2*id)+1,mid,r,index,value);
combine(id);
return;
}

};
    int minimumDifference(vector<int>& nums, int k) {
        int mxd=INT_MAX;
        int n=nums.size();
        SGtree s1(n);
 s1.build(1,0,n,nums);
        for(int i=0;i<nums.size();i++){
            int ele=nums[i];
            if(ele<=k){
                mxd=min(mxd,k-ele);
            }
            else{

                int l=i;
                int r=nums.size()-1;
                while(l<=r){
                    int mid=(int)l+r;
                    mid=mid/2;
                    int val=s1.query(1,i,mid+1,0,n);
                    if(val>=k){
                        mxd=min(mxd,abs(val-k));
                        l=mid+1;
                    }
                    else{
                        mxd=min(mxd,abs(val-k));
                        r=mid-1;

                    }


                }
            }

        }
        return mxd;
        //return 0;

    }
};
