class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        subarray = set()
        answer = math.inf
        for n in nums:
            subarray = {v & n for v in subarray}
            subarray.add(n)
            for v in subarray:
                if (value := abs(k - v)) < answer:
                    answer = value
        return answer
