class Solution {
public:
    void build(int s,int e,int pos,vector<int> &st,vector<int> &nums){
        if(s==e){
            st[pos]=nums[s];
            return;
        }
        int mid=(s+e)/2;
        build(s,mid,2*pos+1,st,nums);
        build(mid+1,e,2*pos+2,st,nums);
        st[pos]=st[2*pos+1]&st[2*pos+2];
    }
    int query(int ql,int qr, int s, int e,int pos, vector<int> &st){
        if(ql<=s && qr>=e){
            return st[pos];
        }
        if(ql>e || qr<s){
            return (int)((1LL<<32)-1);
        }
        int mid=(s+e)/2;
        return query(ql,qr,s,mid,2*pos+1,st)&query(ql,qr,mid+1,e,2*pos+2,st);
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> st(4*n);
        build(0,n-1,0,st,nums);
        int ans=INT_MAX;
        for(int i=0;i<n;i++){
            int l=0,r=i;
            int mid;
            while(l<=r){
                mid=(l+r)/2;
                int p=query(mid,i,0,n-1,0,st);
                if(p<=k){
                    l=mid+1;
                }
                else{
                    r=mid-1;
                }
                ans=min(ans,abs(p-k));
            }
            if(ans==0){return 0;}
            if(l<=i){
                int p=query(l,i,0,n-1,0,st);
                ans=min(ans,abs(p-k));
            }
        }
        return ans;
    }
};
