class Solution(object):
    def minimumDifference(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        def getandvalue(dictionary, start, end):
            andvalue=0
            for i in range (32):
                if len(dictionary[i])==end-start:
                    andvalue+=2**i
            return andvalue
        closest= float('inf')
        start=0
        end=0
        placevalues={}
        for i in range (32):
            placevalues[i]=set()
        while end<=len(nums):
            relevantval=getandvalue(placevalues ,start, end)
            # print("STARTINFO")
            # print(closest)
            # print(relevantval)
            # print (abs(closest-k))
            # print(abs(k-relevantval))
            # print("ENDINFO")
            if abs(closest-k)>abs(k-relevantval):
                # print("here1")
                closest=relevantval
            if relevantval > k:

                #extend end
                if end == len(nums):
                    break
                addingnum = nums[end]
                counter=0
                while addingnum:

                    if addingnum%2==1:
                        placevalues[counter].add(end)
                    counter+=1
                    addingnum = addingnum//2
                end+=1
            elif relevantval < k:

                #shorten start
                for i in range (32):
                    if start in placevalues[i]:
                        placevalues[i].remove(start)
                start+=1

            else:
                return 0
            # print(relevantval)
            # print(closest)
            # print(placevalues)
            # print(start)
            # print(end)
        return abs(closest-k)
