class Solution {
   public:
    int minimumDifference(vector<int>& nums, int t) {
        int p, q, ret = 2147483647;
        int i, n = nums.size();
        unordered_map<int, int> jaja;
        unordered_map<int, int> memo;
        unordered_map<int, int> momo;

        for (i = 0; i < n; i++) {
            p = nums[i];
            for (auto [k, v] : memo)
                q = k & p,
                momo[q]++,
                jaja[q]++;

            momo[p]++;
            jaja[p]++;
            memo = momo;
            momo.clear();
        }
        // for (auto [k, v] : jaja)
        //     cout << k << " ";
        cout << endl;
        for (auto [k, v] : jaja)
            ret = min(ret, abs(k - t));

        return ret;
    }
};
