class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        // for (int i = 0; i < nums.size(); i++) {
        //     int cur = INT_MAX;
        //     for (int j = i; j < nums.size(); j++) {
        //         cur &= nums[j];
        //         cout << cur<<" ";
        //     }
        //     cout << endl;
        // }
        int l = 1 << ((int)ceil(log(nums.size()) / log(2)) + 1);
        tree.resize(l, INT_MAX);
        int res = INT_MAX;
        for (int i = 0; i < nums.size(); i++) {
            put(0, nums.size(), 1, i, nums[i]);
            // cout << i << " " << tree[1] << endl;
            search(0, nums.size(), 1, i + 1, k, INT_MAX, res);
        }
        return res;
    }

private:
    vector<int> tree;

    int put(int i, int j, int k, int x, int v) {
        if (i > x || j <= x) return INT_MAX; // [i, j)
        if (i == j - 1) {
            tree[k] &= v;
            return tree[k];
        }
        int mid = (i + j) / 2;
        int l = put(i, mid, k << 1, x, v);
        int r = put(mid, j, (k << 1) + 1, x, v);
        return tree[k] = tree[k] & l & r;
    }

    int search(int i, int j, int k, int hi, int v, int t, int& res) {
        if (i >= hi || res == 0) return INT_MAX;
        if (i == j - 1) {
            res = min(res, abs(v - (t & tree[k])));
            return tree[k];
        }
        if (j <= hi && ((tree[k] & t) >= v)) {
            // cout << i << " " << j << " " << k << " " << tree[k] << " " << t << " " << v << endl;
            res = min(res, (t & tree[k]) - v);
            return tree[k];
        }
        int mid = (i + j) / 2;
        int r = search(mid, j, (k << 1) + 1, hi, v, t, res);
        if ((t & r) <= v) {
            res = min(res, v - (t & r));
            return r;
        }
        else if (r != INT_MAX) {
            res = min(res, (t & r) - v);
        }
        int l = search(i, mid, (k << 1), hi, v, t & r, res);
        return l & r;
    }
};
