class Solution {
public:
    int findand(int i,int j,vector<vector<int>> &spt){
        int andd=spt[0][i];
        for(int k=24;k>=0;k--){
            if((1<<k)<=(j-i+1)){
                andd=andd & (spt[k][i]);
                i+=(1<<k);
            }
        }
        return andd;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> spt(25);

        for(int i=0;i<n;i++){
            spt[0].push_back(nums[i]);
        }

        for(int i=1;i<25;i++){
            for(int j=0;j+(1<<i)<=n;j++){
                spt[i].push_back(spt[i-1][j] & spt[i-1][j+(1<<(i-1))]);
            }
        }

        int ans=INT_MAX;
        int i=0,j=0,cur=nums[0];
        while(j<n){
            cur=findand(i,j,spt);
            ans=min(ans,abs(cur-k));
            if(cur==k)return 0;
            if(cur<k){
                i++;
                if(i>j)j=i;
            }
            else{
                j++;
            }
        }

        return ans;
    }
};
