class Ishu
{
public:
    vi t;
    int n;

    Ishu(vi &a)
    {
        n = a.size();
        t.resize(4 * n);
        build(a, 0, 0, n - 1);
    }

    void build(vi &a, int node, int start,
               int end)
    {
        if (start == end)
        {
            t[node] = a[start];
        }
        else
        {
            int mid = (start + end) / 2;
            build(a, 2 * node + 1, start, mid);
            build(a, 2 * node + 2, mid + 1, end);
            t[node] = t[2 * node + 1] & t[2 * node + 2];
        }
    }

      int q(int l, int r)
    {
        return q(0, 0, n - 1, l, r);
    }

    int q(int node, int start, int end, int l, int r)
    {
        if (r < start || end < l)
        {
            return INT_MAX;
        }
        if (l <= start && end <= r)
        {
            return t[node];
        }
        int mid = (start + end) / 2;
        int p1 = q(2 * node + 1, start, mid, l, r);
        int p2 = q(2 * node + 2, mid + 1, end, l, r);
        return p1 & p2;
    }
};

class Solution
{
public:
    int minimumDifference(vi &a, int k)
    {
        int n = sz(a);
        Ishu st(a);
        int ans = INT_MAX;
        loop(i, 0, n)
        {
            int l = i - 1, r = n - 1, mid;
            while (r - l > 1)
            {
                mid = (l + r) / 2;
                if (st.q(i, mid) >= k)
                    l = mid;
                else
                    r = mid;
            }
            if (l != i - 1)
                ans = min(ans, abs(st.q(i, l) - k));
            ans = min(ans, abs(k - st.q(i, r)));
        }
        return ans;
    }
};
