class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<pair<int,int>> temp2;
        int ans=INT_MAX;
        int let=nums[0];
        for(int i=0;i<nums.size();i++){
            ans=min(ans,abs(k-nums[i]));
            temp2.push_back({nums[i],i});
            let&=nums[i];
        }
        for(int i=31;i>=0;i--){
            vector<pair<int,int>> temp;
            for(int j=0;j<temp2.size();j++){
                if((temp2[j].first&(1<<i))){
                    temp.push_back(temp2[j]);
                }
            }
            if(temp.size()!=0){
                int curr=temp[0].first;
                int prev=temp[0].second;
                for(int j=1;j<temp.size();j++){
                    if(temp[j].second!=prev+1){
                        prev=temp[j].second;
                        curr=temp[j].first;
                    }
                    else{
                        prev++;
                        curr=curr&temp[j].first;
                    }
                    ans=min(ans,abs(k-curr));
                }
            }

        }
        ans=min(ans,abs(k-let));
        return ans;
    }
};
