class Solution {
public:
    int s[100005][18];
    void build(int n){
        for(int i=1; i<=17; i++){
            for(int j=0; j+(1<<i)-1<n; j++){
                s[j][i]=(s[j][i-1]&s[j+(1<<(i-1))][i-1]);
            }
        }
    }
    ll query(int l, int r){
        ll len=r-l+1;
		ll downto=31-__builtin_clz(len);
		ll ans=(s[l][downto]&s[r-(1<<downto)+1][downto]);
		return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        fr(n) cout<<bitset<10>(nums[i])<<nl;
        fr(n) s[i][0]=nums[i];
        build(n);
        ll ans=inf;
        fr(n){
            int lst=i-1;
            while(1){
                if(lst+1>=n) break;
                int l=lst+1, r=n-1, now=lst+1;
                while(l<=r){
                    int mid=(l+r)/2;
                    ll qval=query(i,mid);
                    if(qval==query(i,lst+1)){
                        now=mid, l=mid+1;
                    }
                    else r=mid-1;
                }

                // cout<<i<<" "<<lst+1<<" "<<now<<" "<<nums[lst+1]<<nl;
                ans=min(ans,(ll)abs(query(i,lst+1)-k));
                lst=now;
            }
