class Solution {
public:
    int sparse[25][200000];

    int minimumDifference(vector<int>& a, int k) {
        int n = a.size();
        for(int i=0;i<n;i++){
            sparse[0][i]=a[i];
        }
        for(int i=1;i<25;i++){
            for(int j=0;j<n-((1<<i)-1);j++){
                sparse[i][j]=((sparse[i-1][j])&(sparse[i-1][j+(1<<(i-1))]));
            }
        }
        bool boom1 = false,boom2 = false;
        int ans1 = 1e9,ans2 = 1e9;
        for(int i = 0 ; i < n ; i++){
            int lo = i,hi = n - 1;
            while(lo <= hi){
                int mid = (hi + lo)/2;
                int dist = log2(mid - i + 1);
                int need=(sparse[dist][i]&sparse[dist][mid-(1<<dist)+1]);
                if(need <= k){
                    boom1 = true;
                    ans1 = min(ans1,abs(k - need));
                    hi = mid - 1;
                }
                else{
                    lo = mid + 1;
                }
            }
        }
        for(int i = 0 ; i < n ; i++){
            int lo = i,hi = n - 1;
            while(lo <= hi){
                int mid = (hi + lo)/2;
                int dist = log2(mid - i + 1);
                int need=(sparse[dist][i]&sparse[dist][mid-(1<<dist)+1]);
                if(need >= k){
                    ans2 = min(ans2,abs(k - need));
                    boom2 = true;
                    lo = mid + 1;
                }
                else{
                    hi = mid - 1;
                }
            }
        }
        int ans = 1e9;
        if(boom1){
            ans = min(ans,ans1);
        }
        if(boom2){
            ans = min(ans,ans2);
        }
        return ans;
    }
};
