class Solution {
public:
    int minimumDifference(vector<int>& a, int k) 
    {
        int n = a.size();
        set<int> st1 , prev , curr; // st1 - distinct Ands

        for( int i = n - 1; i >= 0; i-- )
        {
            if( prev.empty() ) curr.insert( a[i] ) , st1.insert( a[i] );
            else
            {
                for( auto& ele : prev ) curr.insert( (ele&a[i]) ) , st1.insert( (ele&a[i]) );

                curr.insert( a[i] );
                st1.insert( a[i] );
            }

            prev = curr;
            curr.clear();
        }

        // for( auto& ele: st1 )
        // {
        //     cout << "ele " << ele << ' ';
        // }
        // cout << '\n';

        int ans = 1e9 + 4;
        for( auto& val : st1 )
        {
            ans = min( ans , abs(k - val) );
        }

        return ans;
    }
};
