class Solution {
    int getAnd(vector<vector<int>>&v, int i, int j){
        if(j<i) return INT_MAX;
        int ans = 0;
        for(int x=0;x<32;x++){
            int a = j-i+1;
            int b = v[x][j];
            if(i>0) b-= v[x][i-1];
            if(a==b) ans|= (1<<x);
        }
        return ans;
    }
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> v(32,vector<int>(n,0));
        for(int x=0;x<32;x++){
            int mask = 1<<x;
            for(int i=0;i<n;i++){
                if(i>0) v[x][i] = v[x][i-1];
                v[x][i] += (nums[i]&mask)!=0;
            }
        }
        int ans = INT_MAX;
        for(int i=0;i<n;i++){
            ans = min(ans,abs(k-nums[i]));
            int low = i;
            int high = n-1;
            while(low<high){
                int mid = low + ((high-low)/2);
                int andvalue = getAnd(v,i,mid);
                ans = min(ans,abs(k-andvalue));
                if(andvalue>k) low=mid+1;
                else if(andvalue<k) high=mid-1;
                else break;
            }
            ans = min(ans,abs(k-getAnd(v,i,high)));
            ans = min(ans,abs(k-getAnd(v,i,low)));
        }
        return ans;
    }
};
