class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        best = abs(k - nums[0])
        length = len(nums)
        a = [0 for i in range(length)]
        prev = [0 for i in range(length)]
        a[0] = nums[0]
        prev[0] = -1
        for i in range(1, length):
            a[i] = nums[i]
            prev[i] = i - 1
            if abs(k - a[i]) < best:
                best = abs(k - a[i])
            last = i
            while prev[last] >= 0:
                j = prev[last]
                a[j] = a[j] & nums[i]
                if a[j] == a[last]:
                    prev[last] = prev[j]
                else:
                    last = j
                    if abs(k - a[j]) < best:
                        best = abs(k - a[j])
        return best
