class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int i = 0, j = 0;
        // vector<int> freq(31, 0);
        // while(j < n){
        //     // and operation
        //     int e = nums[j];
        //     int val = 0;
        //     for(int ind=0; ind<31; ind++){
        //         if(e & (1<<ind)){
        //             freq[ind]++;
        //         }
        //         if(freq[ind] == j-i+1){
        //             val = val | (1<<ind);
        //         }
        //     }

        //     if()
        // }
        vector<vector<int>> freq(n, vector<int>(31, 0));
        for(int i=0; i<n; i++){
            int e = nums[i];
            for(int ind=0; ind<31; ind++){
                freq[i][ind] = ((i > 0) ? freq[i-1][ind] : 0);
                if(e & (1<<ind)){
                    freq[i][ind]++;
                }
            }
        }
        int mini_diff = 1e9;
        for(int i=0; i<n; i++){
            int low = i, high = n-1;
            while(low <= high){
                int mid = low + ((high - low) >> 1);
                int val = 0;
                for(int ind=0; ind<31; ind++){
                    int cnt = freq[mid][ind] - ((i>0)?freq[i-1][ind]:0);
                    if(cnt == mid-i+1){
                        val = val | (1<<ind);
                    }
                }
                mini_diff = min(mini_diff, abs(k-val));
                // cout<<i<<", "<<mid<<", "<<val<<endl;
                if(k < val){
                    low = mid + 1;
                }else if(k > val){
                    high = mid - 1;
                }else if(k == val){
                    return 0;
                }
            }
        }
        return mini_diff;
    }
};
