class Solution {
public:
    int andV(vector<int>& bits, int b, int n) {
        int res = 0;
        int i = 0;

        while (b) {
            if (b & 1)
            {
                if (++bits[i] == n)
                    res |= 1 << i;
            }

            b >>= 1;
            i++;
        }

        return res;
    }

    int removeAnd(vector<int>& bits, int b, int n) {
        int res = 0;

        for (int i=0; i<bits.size(); ++i) {
            if (b & 1) {
                bits[i]--;
            }

            if (n != 0 && bits[i] == n)
                res |= 1 << i;

            b >>= 1;
        }

        return res;
    }

    int minimumDifference(vector<int>& nums, int k) {
        const int N = 32;
        int res = INT_MAX;

        int l = 0;
        vector<int> bits(N, 0);

        for (int r=0; r<nums.size(); ++r) {
            int n = r - l + 1;
            int num = andV(bits, nums[r], n);

            // cout << r << ": " << nums[r] << " -> " << num << endl;

            while (num < k && l <= r) {
                res = min(res, k - num);
                num = removeAnd(bits, nums[l++], --n);
                // cout << l << ": " << nums[l-1] << " -> " << num << endl;
            }

            if (l <= r)
                res = min(res, abs(k - num));
        }

        return res;
    }
};
