class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        set<int> prev;
        int n = 0;
        int minV = INT_MAX;
        for (int i = 0; i < nums.size(); i++){
            set<int> next;
            for (auto it = prev.begin(); it != prev.end(); it++)
                next.insert((*it) & nums[i]);
            next.insert(nums[i]);

            for (auto it = next.begin(); it != next.end(); it++)
                minV = min(minV, abs(k - (*it)));

            prev = next;
        }
        return minV;
    }
};
