class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        int ans=1e9,n=a.size();
        vector<vector<int>> b(n,vector<int>(31,n));
        for(int i=0;i<=30;i++)
        {
            stack<int> st;
            for(int j=0;j<n;j++)
            {
                while(!st.empty() && !(a[j]&(1<<i)))
                {
                    b[st.top()][i]=j;
                    st.pop();
                }
                st.push(j);
            }
        }
        set<int> s;
        for(int i=0;i<n;i++)
        {
            sort(b[i].begin(),b[i].end());
            int subans=a[i];
            s.insert(subans);
            for(auto x:b[i])
            {
                if(x==n)
                break;
                subans&=a[x];
                s.insert(subans);
            }
        }
        for(auto x:s)
        ans=min(ans,abs(k-x));
        return ans;
    }
};
