class Solution {
public:
    int minimumDifference(vector<int>& A, int k) {
        int n = A.size(), mn = INT_MAX;

        vector<array<int, 30>> bits(n);
        for (int j = 0; auto a : A) {
            for (int i = 0; i < 30; ++i) {
                if (1 << i & a) ++bits[j][i];
                if (j > 0) bits[j][i] += bits[j - 1][i];
            }
            ++j;
        }


        const auto get_val = [&](auto l, auto r) {
            int res = 0;
            for (int i = 0; i < 30; ++i) {
                if (bits[r][i] - (l == 0 ? 0 :  bits[l - 1][i]) == r - l + 1)
                    res += 1 << i;
            }
            return res;
        };

        const auto solve = [&](auto i) {
            const auto ok = [&](auto m) {
                return get_val(i, m) >= k;
            };

            int l = i, r = n - 1;
            while (l < r) {
                auto m = l + (r - l) / 2;
                if (ok(m)) l = m + 1;
                else r = m;
            }
            return max(i, l == n || !ok(l) ? l - 1 : l);
        };


        for (int i = 0; i < n; ++i) {
            auto r = solve(i);
            for (int x : {r - 1, r, r + 1}) {
                if (x >= i && x < n) mn = min(mn, abs(get_val(i, x) - k));
            }
        }

        return mn;
    }
};
