using LL = long long;
using PII = pair<int, int>;
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<PII> q;
        LL res = INT_MAX;
        for (int i = 0; i < nums.size(); ++i) {
            q.push_back({~0, i});
            int j = 0;
            for (auto &[v, left] : q) {
                v &= nums[i];
                res = min(res, abs((LL)v - (LL)k));
                if (q[j].first == v) {
                    q[j].second = left;
                } else {
                    q[++j] = {v, left};
                }
            }
            q.resize(j + 1);
        }
        return res;
    }
};
