class SegmentTree {
    vector<int> tree;
    int size;

    void buildTree(vector<int>& nums, int start, int end, int node) {
        if (start == end) {
            tree[node] = nums[start];
        } else {
            int mid = (start + end) / 2;
            buildTree(nums, start, mid, 2 * node + 1);
            buildTree(nums, mid + 1, end, 2 * node + 2);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    int queryRange(int start, int end, int left, int right, int node) {
        if (start > right || end < left) {
            return INT_MAX;  
        }
        if (start >= left && end <= right) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int leftResult = queryRange(start, mid, left, right, 2 * node + 1);
        int rightResult = queryRange(mid + 1, end, left, right, 2 * node + 2);
        return leftResult & rightResult;
    }

public:
    SegmentTree(vector<int>& nums) {
        size = nums.size();
        int maxSize = 4 * size; 
        tree.resize(maxSize);
        buildTree(nums, 0, size - 1, 0);
    }

    int queryRange(int left, int right) {
        return queryRange(0, size - 1, left, right, 0);
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        SegmentTree segmentTree(nums);
        int Diff = INT_MAX;

        for (int start = 0; start < n; ++start) {
            int low = start, high = n - 1;
            while (low <= high) {
                int mid = (low + high) / 2;
                int andValue = segmentTree.queryRange(start, mid);
                int d = abs(k - andValue);
                Diff = min(Diff,d);
                if (andValue > k) {
                    low = mid + 1;
                } else {
                    high = mid - 1;
                }
            }
        }

        return Diff;
    }
};
