class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) 
    {
        int n = nums.size();
        vector<int> temp(32);
        int l = 0, r = 0;
        for(int i = 0; i < 32; ++i)
        {
            if((nums[0] >> i) & 1)
            {
                temp[i] += 1;
            }
        }

        int curr = 0;
        for(int i = 0; i < 32; ++i)
        {
            if((temp[i] == (r-l+1)))
            {
                curr += (1 << i);
            }
        }
        // cout << curr << endl;
        int ans = INT_MAX;
        ans = min(ans, abs(k - curr));
        while(r < n) //
        {
            if(curr <= k)
            {
                for(int i = 0; i < 32; ++i)
                {
                    if((nums[l] >> i) & 1)
                    {
                        temp[i] -= 1;
                    }
                }
                l++;
                if(r < l)
                {
                    r++;
                    if(r == n)
                    {
                        break;
                    }
                    for(int i = 0; i < 32; ++i)
                    {
                        if((nums[r] >> i) & 1)
                        {
                            temp[i] += 1;
                        }
                    }
                }
                curr = 0;
                for(int i = 0; i < 32; ++i)
                {
                    if((temp[i] == (r-l+1)))
                    {
                        curr += (1 << i);
                    }
                }
            }
            else
            {
                r++;
                if(r == n)
                {
                    break;
                }
                for(int i = 0; i < 32; ++i)
                {
                    if((nums[r] >> i) & 1)
                    {
                        temp[i] += 1;
                    }
                }
                curr = 0;
                for(int i = 0; i < 32; ++i)
                {
                    if((temp[i] == (r-l+1)))
                    {
                        curr += (1 << i);
                    }
                }
            }
            cout << curr << endl;
            ans = min(ans, abs(k - curr));
        }
        return ans;
    }
};
