class Solution {
    int make(vector<int>& a, int l, int r, vector<vector<int>>& pref){
        int n = a.size();
        int res = 0;
        for(int j = 0; j < 31; j++){
            if(pref[r+1][j]-pref[l][j]==r-l+1) res+=(1<<j);
        }
        return res;
    }
public:
    int minimumDifference(vector<int>& a, int k) {
        int n = a.size();
        vector<vector<int>> pref(n+1, vector<int>(32, 0));
        for(int i = 0; i < n; i++){
            for(int j = 0; j < 31; j++){
                pref[i+1][j] = pref[i][j]+((a[i]>>j)&1);
            }
        }
        int ans = abs(a[0]-k);
        for(int i = 0; i < n; i++){
            int lo = i, hi = n-1, mn = a[i];
            while(lo<=hi){
                int mid = (lo+hi)/2;
                int val = make(a, i, mid, pref);
                if(val<k) {
                    hi = mid-1;
                    ans=min(ans, k-val);
                } else {
                    lo = mid+1;
                    ans=min(ans, val-k);
                } 
            }
        }
        return ans;
    }
};
