class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> pref(n,vector<int>(30,0));

        for(int b=0;b<30;b++){

            for(int i=0;i<n;i++){
                if(i)pref[i][b] += pref[i-1][b];
                if(((1<<b)&nums[i])){
                    pref[i][b]++;
                }
            }
        }


        const int inf = 1e9+10;
        int ans = inf;


        for(int i=0;i<n;i++){
            ans = min(ans,abs(k-nums[i]));

            int andd = 0;
            for(int b=0;b<30;b++){
                if(pref[n-1][b] - (i == 0 ? 0 : pref[i-1][b]) == n-i)andd += (1<<b);
            }

            if(k >= nums[i]){
                ans = min(ans,k-nums[i]);
                continue;
            }
            if(andd >= k){
                ans = min(andd-k,ans);
                continue;
            }

            int l = i+1,r = n-1;

            while(l < r){
                int mid = (l + r)/2;

                int andd = 0;
                for(int b=0;b<30;b++){
                    if(pref[mid][b] - (i == 0 ? 0 : pref[i-1][b]) == mid-i+1)andd += (1<<b);
                }

                if(andd <= k)
                    r = mid;
                else
                    l = mid + 1;
            }

            andd = 0;

            for(int b=0;b<30;b++){
                    if(pref[l][b] - (i == 0 ? 0 : pref[i-1][b]) == l-i+1)andd += (1<<b);
            }

            ans = min(ans,abs(k-andd));

            l--;

            if(l >= i){
                andd = 0;

                for(int b=0;b<30;b++){
                    if(pref[l][b] - (i == 0 ? 0 : pref[i-1][b]) == l-i+1)andd += (1<<b);
                }   
            }
            ans = min(ans,abs(k-andd));
        }

        return ans;
    }
};
