class Solution {
public:
    int calc(int num,vector<int> &bit,int left,int right){
        int n1=num;
        int ind=0;
        while(n1){
            if(n1%2==1){
                bit[ind]++;
            }
            ind++;
            n1>>=1;
        }
        int ans=0;
        for(int i=0;i<32;i++){
            if(bit[i]==(right-left+1)){
                ans+=(1<<i);
            }
        }
        return ans;
    }
    void remove(int num,vector<int> &bit,int left,int right){
        int n1=num;
        int ind=0;
        while(n1){
            if(n1%2==1){
                bit[ind]--;
            }
            ind++;
            n1>>=1;
        }
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        int left=0;
        int ans=INT_MAX;
        int x=0;
        vector<int> bit(32,0);
        for(int right=0;right<n;right++){
            x=calc(nums[right],bit,left,right);

            ans=min(ans,abs(x-k));
            if(x<k){
                while(left<right && x<k){
                    left++;
                    remove(nums[left-1],bit,left,right);
                    x=calc(0,bit,left,right);
                    ans=min(ans,abs(x-k));
                }
            }
        }
        return ans;
    }
};
