class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        unordered_set<int>prev;
        int n=nums.size();
        int ans=INT_MAX;
        for(int i=0;i<n;i++){
            unordered_set<int>cur;
            for(auto it:prev){
                cur.insert(nums[i]&it);
}
           cur.insert(nums[i]);
            for(auto x:cur){
                ans=min(ans,abs(k-x));
            }
            prev=cur;

        }
        return ans;



    }
};
