class Solution {
public:

    int calc(vector<int>&v,int n)
    {
        int a=0;
        for(int i=0;i<32;i++)
        {
            if(v[i]==n)
            {
                a=a|1<<i;
            }
        }
        return a;
    }

    int minimumDifference(vector<int>& a, int k) {
        int s=0,e=0,as=INT_MAX;
        int n=a.size();
        vector<int>cv(32,0);
        for(e=0;e<n;e++)
        {
            int c=a[e];
            for(int i=0;i<32;i++)
            {
                if(1<<i & c)    cv[i]++;
            }

            int l=e-s+1;
            int nv=calc(cv,l);
            // cout<<"\ninitially-at-"<<e;
            // cout<<"\tval is "<<nv;
            as=min(abs(nv-k),as);

            while(s<=e and nv<k)
            {
                // cout<<"\nremoving "<<s;
                c=a[s];
                for(int i=0;i<32;i++)
                {
                    if(1<<i & c)    cv[i]--;
                }
                s++;
                l=e-s+1;
                nv=calc(cv,l);
                as=min(as,abs(nv-k));
            }
            // cout<<"\nat "<<e<<" s-became "<<s;
            as=min(abs(nv-k),as);

        }


        return as;

    }
};
