const int N = 1e5 + 1;
int dp[N + 1][21]; 
class Solution {

    int ans = INT_MAX;
    int n, k;
    int maxj;

    void dfs(int cur, int prev) {
        if(cur >= n) {
            return;
        }
        if(prev < k) {
            return;
        }
        auto& v = dp[cur];


        int lo = 0, hi = maxj + 1;

        // cout << "cur=" << cur << " prev=" << prev << endl;
        // cout << "v=";
        // for(int j = 0; j <=maxj; ++j) {
        //     cout << v[j] <<" ";
        // }
        // cout << endl;

        while(lo < hi) {
            int mid = (lo + hi + 1) / 2;
            if(v[mid] == -1 || (v[mid] & prev) < k) {
                hi = mid - 1;
            }else{
                lo = mid;
            }
        }
        ans = min(ans, abs((v[lo] & prev) - k));
        if(ans == 0) {
            return;
        }
        // cout << "lo=" << lo << " val=" << (v[lo] & prev) << " abs=" << abs((v[lo] & prev) - k) << endl;
        int len = 1 << lo;
        dfs(cur + len, prev & v[lo]);
    }

public:
    int minimumDifference(vector<int>& A, int k) {
        n = A.size();
        this->k = k;
        memset(dp, 0xff, sizeof(dp));
        for(int i = 0; i < n; ++i) {
            dp[i][0] = A[i];
        }
        maxj = 0;
        for(int j = 1, len = 2; len <= n; ++j, len <<= 1) {
            maxj = j;
            for(int i = 0; i < n; ++i) {
                if(i + len > n) {
                    break;
                }
                dp[i][j] = dp[i][j - 1] & dp[i + len/2][j - 1];
            }
        }

        // for(int i = 0; i < n; ++i) {
        //     for(int j = 0; j <= maxj; ++j) {
        //         cout << dp[i][j] << " ";
        //     }
        //     cout << endl;
        // }
        for(int i = 0; i < n; ++i) {
            // cout << "new dfs" << endl;
            dfs(i, INT_MAX);
            if(ans == 0) {
                return 0;
            }
        }

        return ans;
    }
};
