class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {


        int n = nums.size();
        set<int> ss;
        set<int> good;

        for(int i = n-1 ; i >= 0 ; i --)
        {
            set<int> cc;
            for(int x : ss)
            {
                cc.insert(x&nums[i]);
            }
            cc.insert(nums[i]);
            ss = cc;
            for(int p : ss) good.insert(p);
        }


        int ans = INT_MAX;
        for(int p : good)
        {
            ans = min(ans , abs(k -p));
        }

        return ans;

    }
};
