class Solution:
    def minimumDifference(self, nums: List[int], target: int) -> int:
        v, nv = set(), set()
        n = len(nums)
        for i in range(n - 1, -1, -1):
            nv = {nums[i] & x for x in nv}
            nv.add(nums[i])
            v |= nv

        return min(abs(x - target) for x in v)
