class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        def solve(nums,i,curr,dp):
            if i==len(nums):
                return abs(k-curr)
            if dp[(i,curr)]:
                return dp[(i,curr)]
            a=solve(nums,len(nums),curr,dp)
            b=solve(nums,i+1,curr&nums[i],dp)
            dp[(i,curr)]=min(a,b)
            return dp[(i,curr)]
        ans=2**32-1
        dp=defaultdict(int)
        for i in range(len(nums)):
            ans=min(ans,solve(nums,i,2**32-1,dp))
        return ans
