class Solution {
public:
	#define maxi 100100
    int checkPref[32][maxi];
    void findPrefixCount(vector<int> &arr, int n)
    {
        for (int i = 0; i < 32; i++) {

            checkPref[i][0] = ((arr[0] >> i) & 1);
            for (int j = 1; j < n; j++) {
                checkPref[i][j] = ((arr[j] >> i) & 1);
                checkPref[i][j] += checkPref[i][j - 1];
            }
        }
    }
    int rangeAnd(int l, int r)
    {
        int ans = 0;

        for (int i = 0; i < 32; i++) {
            int x;
            if (l == 0)
                x = checkPref[i][r];
            else
                x = checkPref[i][r]
                    - checkPref[i][l - 1];
       if (x == r - l + 1)
                ans = (ans | (1 << i));
        }

        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        vector<int>pref(32);
        int n = nums.size();
        findPrefixCount(nums, n);
        int indx = 1;
        int ans = INT_MAX;
        for(auto x : nums)
        {
            for(int i = 0; i < 32; i++)
            {
                if((x & ( 1 << i)) == 0)
                {
                    pref[i] = indx;   
                }
            }
            int mx = 0, mn = indx;
            int num = 0;
            for(int i = 31; i >= 0; i--)
            {
                if((k&(1<<i)))
                {
                    if((pref[i]+1) <= mn)
                    {
                        mx = max(mx, pref[i]+1);
                        num |= (1<<i);
                    }
                }
                else 
                {
                    if(pref[i] && pref[i] >= mx)
                    {
                        mn = min(mn, pref[i]);
                    }
                    else num |= (1<<i);
                }
                int curAns = rangeAnd(pref[i], indx-1);
                if(curAns != -1) ans = min(ans, abs(curAns - k));
            }
            ans = min(abs(num - k), ans);
            indx++;
        }
        return ans;
    }
};
