class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> cnt(31,0);
        int mini = INT_MAX;
        int l=0,r=-1;
        for(int i=0; i<n; i++){
            r++;
            int val=0;
            for(int j=0; j<31; j++){
                if(nums[r]&(1<<j)){
                    cnt[j]++;
                    if(cnt[j]==r-l+1) val+=(int)(1<<j);
                }
            }
            //cout<<"val " <<val<<endl;
            mini = min(mini,abs(k-val));
            while(l<r&&k>val){
                l++;
                val=0;
                for(int j=0; j<31; j++){
                    if(nums[l-1]&(1<<j)){
                        cnt[j]--;
                    }
                    if(cnt[j]==r-l+1) val+=(int)(1<<j);
                }
                mini = min(mini,abs(k-val));
                //cout<<"val " <<val<<endl;
                //cout<<"l "<<l<<endl;
            }
            //cout<<"l r"<<l<<" "<<r<<endl;
        }
        return mini;
    }
};
