class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        mn = inf
        s=0
        sets =[set(), set()]
        for x in nums:
            for y in sets[s]:
                sets[1-s].add(x&y)
            sets[1-s].add(x)
            sets[s].clear()
            s = 1-s
            for x in sets[s]:
                mn = min(mn, abs(k-x))
        return mn
