class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        res = inf
        mem = set()
        def pick(i, cur):
            nonlocal res
            if (i, cur) in mem:
                return
            mem.add((i, cur))
            if k - cur >= res:
                return
            res = min(res, abs(k - cur))
            if i == len(nums):
                return
            pick(i + 1, cur & nums[i])

        for i in range(len(nums)):
            res = min(res, abs(k - nums[i]))
            pick(i + 1, nums[i])

        return res
