class Solution {
public:

    int findAnd(vector<vector<int> > &pref,int l, int r){
        if(r >= pref.size()){
            return 0;
        }
        int ans = 0;
        long long mul = 1;

        for(int j =0;j<32;j++){
            int here = pref[r][j] - ((l > 0) ? pref[l-1][j] : 0);
            if(here == r-l+1){
                ans += (mul);
            }
                mul *= 2LL;
        }
        return ans;
    }

    int minimumDifference(vector<int>& a, int k) {

        int n = a.size();
        vector<vector<int> > pref(n, vector<int>(32,0));

        for(int i = 0;i<a.size();i++){
            int x = a[i],idx = 0;
            while(x > 0){
                if(x%2 == 1){
                    pref[i][idx] = 1;
                }
                x = x/2;
                idx++;
            }
        }

        for(int i = 1;i<a.size();i++){
            for(int j = 0;j<32;j++){
                pref[i][j] += pref[i-1][j];
            }
        }

        int global = abs(k - a[0]);

        for(int i = 0;i<n;i++){
            // find lower bound here

            int l = i, r = n;

            while(l +1 < r){
                int mid = (l+r)/2;
                int here = findAnd(pref, i, mid);
                // cout<<"At "<<i<<" = "<<l<<", "<<mid<<", "<<r<<" val = "<<here<<" \n";                 
                if(here >= k){
                    // cout<<"moving l to "<<mid<<"\n";
                    l = mid;
                }else{
                    // cout<<"moving r to "<<mid<<"\n";
                    r = mid;
                }
            }

            // cout<<"at "<<i<<" found "<<l<<"\n\n";            
            int la = findAnd(pref,i,l);
            int here = abs(la - k);

            if(l+1 < n){
                int la1 = findAnd(pref, i, l+1);
                here = min(here, abs(la1 - k));
            }

            global = min(here, global);

        }
        return global;
    }
};
