class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        q = [nums[0]]

        res = abs(k - q[0])
        for i in range(1, len(nums)):
            tmp = [nums[i]]
            res = min(res, abs(k - tmp[0]))
            for n in q:
                node = n&nums[i]
                res = min(res, abs(k - node))
                if node > k and tmp[-1] != node:
                    tmp.append(node)
            q = tmp
        return res
