class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        int n  = v.size();
        vector <bitset<32>> d (n+1);
        for (int i=1;i<=n;i++) {
            bitset<32> x (v[i-1]);
            d[i]=x;
        }
        // cout<<"hello"<<endl;
        vector <vector <int>> p (n+1, vector <int>(32));
        for (int i=1;i<=n;i++) {
            // cout<<i<<endl;
            for (int j=0;j<32;j++) {
                if (d[i][j]) p[i][j]=1;
                p[i][j]+=p[i-1][j];
            }
        }
        // for (int j=31;j>=0;j--) cout<<p[1][j]-p[0][j]<<" "; cout<<endl;
        auto sol = [&] (int x, int y) {
            x--;
            int l = y-x;
            int val = 0;
            for (int j=31;j>=0;j--) {
                if (p[y][j]-p[x][j]==l) val = ((val<<1) + 1);
                else val = (val <<1);
            }
            // cout<<l<<"===="<<val<<"  "<<x<<' '<<y<<endl;
            return k-val;
        };

        int ans = 2e9;
        // cout<<"hello"<<endl;
        for (int i=1;i<=n;i++) {
            int l = 1, r = i;
            int lans=2e9;
            while (l<=r) {
                int m = (l+r)/2;
                int val = sol(m,i);
                if (val ==0) {lans=0; break;}
                else if (val > 0) lans =  min (lans,abs(val)),l=m+1;
                else lans = min (lans,abs(val)),r=m-1;
            }
            // cout<<i<<' '<<lans<<endl;
            ans=  min (ans,lans);
        }
        return ans;
    }
};
