class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

        int n = int(nums.size());
        int frr[n+1][32];
        for(int i = 0;i<n+1; i++){
            for(int j =0;j< 32;j++){
                frr[i][j] = 0;
            }
        }

        for(int i= 0;i< n; i++){
            for(int j = 0; j< 32; j++){
                if((nums[i]&(1<< j))){
                    frr[i][j]++;
                }
                if(i){
                    frr[i][j] += frr[i-1][j];
                }
            }
        }


        int ans = 1e9;

        for(int i= 0;i<n;i++){

            int l = i, h = n-1;
            int idx;

            while(l<= h){
                int mid = (l+ h)/2;

                int num = 0;
                for(int j = 0;j< 32;j++){
                    if(frr[mid][j]- ((i)? frr[i-1][j]: 0)== mid-i+1){
                        num += (1<< j);
                    }
                }

                if(num<= k){
                    ans= min(ans, k- num);
                    h = mid-1;
                }
                else{
                    l = mid+1;
                }
            }

            l = i, h = n-1;

            while(h>= l){
                int mid= (l+ h)/2;

                int num = 0;
                for(int j = 0;j< 32;j++){
                    if(frr[mid][j]- ((i)? frr[i-1][j]: 0)== mid-i+1){
                        num += (1<< j);
                    }
                }

                if(num>= k){
                    ans = min(ans, num- k);
                    l= mid+1;
                }
                else{
                    h= mid-1;
                }




            }


        }

        for(auto it: nums){
            ans = min(ans, abs(it- k));
        }


        return ans;

    }
};
