class XYZ {
public:
    XYZ(const vector<int>& arr) {
        size = arr.size();
        tree.resize(4 * size);
        foo(arr, 0, 0, size - 1);
    }

    int bar(int L, int R) {
        return bar(0, 0, size - 1, L, R);
    }

private:
    vector<int> tree;
    int size;

    void foo(const vector<int>& arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            foo(arr, 2 * node + 1, start, mid);
            foo(arr, 2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    int bar(int node, int start, int end, int L, int R) {
        if (R < start || end < L) {
            return INT_MAX;
        }
        if (L <= start && end <= R) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int leftResult = bar(2 * node + 1, start, mid, L, R);
        int rightResult = bar(2 * node + 2, mid + 1, end, L, R);
        return leftResult & rightResult;
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& arr, int k) {
        int n = arr.size();
        XYZ xyz(arr);
        int diff = INT_MAX;

        for (int i = 0; i < n; ++i) {
            bf(xyz, arr, i, n, k, diff);
            if (diff == 0) {
                return 0;
            }
        }

        return diff;
    }

private:
    void bf(XYZ& xyz, const vector<int>& arr, int i, int n, int k, int& diff) {
        int lo = i, hi = n - 1;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            int subAnd = xyz.bar(i, mid);
            int curDiff = abs(k - subAnd);
            diff = min(diff, curDiff);

            if (subAnd < k) {
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
    }
};
