class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        d = {}
        res = inf

        for n in nums:
            newd = set()
            for prev in d:
                cur = prev & n
                #print(n, cur, d, res)
                res = min(abs(cur - k), res)
                if cur: newd.add(cur)
                #print(n, cur, d, res)
            d = newd | {n}
            res = min(res, abs(n - k))

        return res
