#include <bits/stdc++.h>
using namespace std;

#define ll long long int
#define INF 1000000000000000000  //1e18
const ll mod = 998244353  ;

#define N 32

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        ll n=nums.size() ;
        ll res = INF ;

        vector<vector<ll>>cnt(n,vector<ll>(N,0)) ;

        for(ll i =0 ; i<n ;i++ ){
            if(i>0){
                for(ll j =0 ; j<N ;j++ ){
                    cnt[i][j] = cnt[i-1][j] ;
                }

            }

            for(ll j =0 ; j<N ;j++ ){
                if( nums[i]&(1LL<<j) ){
                    cnt[i][j]++ ;
                }
            }
        }


        for(ll i =0 ; i<n ;i++ ){

            ll low = i , high =n-1 , index =-1 ;
            while(low<=high){
                ll mid = low + (high-low)/2 ;

                ll andValue = 0 ;
                for(ll j =0; j<N ;j++ ){
                    ll c = cnt[mid][j] - (i>0 ? cnt[i-1][j] :0 ) ;
                    if( c== (mid-i+1) ){
                        andValue+=(1LL<<j) ;
                    }
                }

                if(andValue <= k ){
                    index = mid ;
                    // low =mid+1 ;
                    high = mid-1 ;
                }
                else {
                    // high = mid-1 ;
                    low =mid+1 ;
                }
            }

            if(index !=-1 ){
                ll andValue = 0 ;
                for(ll j =0; j<N ;j++ ){
                    ll c = cnt[index][j] - (i>0 ? cnt[i-1][j] :0 ) ;
                    if( c== (index-i+1) ){
                        andValue+=(1LL<<j) ;
                    }
                }

                res = min(res , abs( andValue - k) ) ;

                if(index !=0 ){
                    index-- ;
                    andValue = 0 ;
                    for(ll j =0; j<N ;j++ ){
                        ll c = cnt[index][j] - (i>0 ? cnt[i-1][j] : 0 ) ;
                        if( c== (index-i+1) ){
                            andValue+=(1LL<<j) ;
                        }
                    }
                    res = min(res , abs( andValue - k) ) ;
                }
            }
            else {
                ll andValue = 0 ;
                for(ll j =0; j<N ;j++ ){
                    ll c = cnt[n-1][j] - (i>0 ? cnt[i-1][j] :0 ) ;
                    if( c== (n-1-i+1) ){
                        andValue+=(1LL<<j) ;
                    }
                }

                res = min(res , abs( andValue - k) ) ;
            }

            // cout<< index <<"\n" ;
        }

        return res ;
    }
};
