class Solution {
public:
    int minimumDifference(vector<int>& v, int k)
    {
        int n = (int)v.size();
        int pre[n][32];
        memset(pre,0,sizeof(pre));

        for(int j=0;j<31;j++) pre[0][j] = ( (v[0] >> j) & 1LL ) ;

        for(int i=1;i<n;i++)
        {
            for(int j=0;j<31;j++) pre[i][j] = pre[i-1][j] + ( (v[i] >> j) & 1LL ) ;
        }

        auto f = [&](int l,int r)
        {
            vector<int> bits(32,0);
            for(int j=0;j<31;j++) bits[j] = pre[r][j] - ( l-1 >= 0 ? pre[l-1][j] : 0 );
            int nd = 0;
            int len = r-l+1;
            for(int j=0;j<31;j++)
            {
                if( bits[j] == len ) nd += (1LL<<j);
            }
            return abs(nd-k);
        };


        auto fval = [&](int l,int r)
        {
            vector<int> bits(32,0);
            for(int j=0;j<31;j++) bits[j] = pre[r][j] - ( l-1 >= 0 ? pre[l-1][j] : 0 );
            int nd = 0;
            int len = r-l+1;
            for(int j=0;j<31;j++)
            {
                if( bits[j] == len ) nd += (1LL<<j);
            }
            return nd;
        };

        int ans = 1e9;
        int r = 0, l = 0;
        int cur = -1;
        int now = 1e9;
        while(r<n)
        {
            if(cur == -1) cur = v[r];
            else cur = cur&v[r];

            now = abs(k-cur);  ans = min(ans,now);

            while(l+1<=r && f(l+1,r) <= now )
            {
                cur = fval(l+1, r);
                l++;
                now = abs(k-cur);
                ans = min(ans,now);
            }

            r++;
        }

        return ans;
    }
};
