class Solution {
    public int minimumDifference(int[] nums, int k) {
        Queue<Integer> q=new LinkedList<>();
        int res=Math.abs(k-nums[0]),n=nums.length;
        q.add(nums[0]);
        for(int i=1;i<n;i++){
            int m=q.size();
            Set<Integer> s=new HashSet<>();
            for(int j=0;j<m;j++){
                int x=q.poll() & nums[i];
                res=Math.min(res,Math.abs(k-x));
                if(!s.contains(x) && k-x<res && x!=0){
                    s.add(x);
                    q.add(x);
                }
            }
            q.add(nums[i]);
            res=Math.min(res,Math.abs(k-nums[i]));
        }
        return res;
    }
}
