class Solution {
    int result = Integer.MAX_VALUE;
    public int minimumDifference(int[] nums, int k) {
        result = Integer.MAX_VALUE;
        Set<Integer> set = new HashSet();
        for (int num : nums) {
        	set = and(set, num, k);
        	if (result == 0)
        		break;
        }
        return result;
    }

	private Set<Integer> and(Set<Integer> orig, int now, int k) {
		Set<Integer> set = new HashSet();
		result = Math.min(result, Math.abs(k - now));
		if (now <= k)
			return set;
		set.add(now);
		for (int v : orig) {
			v &= now;
			result = Math.min(result, Math.abs(k - v));
			if (v > k)
				set.add(v);
		}
		return set;
    }
}
