class SegmentTree {
public:
    vector<int> tree;
    int n;

    void buildTree(const vector<int>& nums, int start, int end, int node) {
        if (start == end) {
            tree[node] = nums[start];
        } else {
            int mid = start + (end - start) / 2;
            buildTree(nums, start, mid, 2 * node + 1);
            buildTree(nums, mid + 1, end, 2 * node + 2);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    int queryTree(int start, int end, int L, int R, int node) {
        if (L <= start && end <= R) {
            return tree[node];
        }
        if (end < L || start > R) {
            return INT_MAX; 
        }
        int mid = start + (end - start) / 2;
        int leftQuery = queryTree(start, mid, L, R, 2 * node + 1);
        int rightQuery = queryTree(mid + 1, end, L, R, 2 * node + 2);
        return leftQuery & rightQuery;
    }

    SegmentTree(const vector<int>& nums) {
        n = nums.size();
        tree.resize(4 * n, INT_MAX);
        buildTree(nums, 0, n - 1, 0);
    }

    int query(int L, int R) {
        return queryTree(0, n - 1, L, R, 0);
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

    int res = INT_MAX;
    SegmentTree seg(nums);
    int lo,hi,mid;

    for (int i = 0; i < nums.size(); i++)
    {
        lo = i - 1;
        hi = nums.size() - 1;

        while (hi - lo > 1)
        {
            mid = lo+ (hi-lo)/ 2;
            int tmp = seg.query(i, mid);
            if ((tmp-k)>= 0) lo = mid;
            else hi = mid;
        }

        int r1 = seg.query(i, lo);
        int r2 = seg.query(i, hi);

        int t1 = abs(r1 - k);
        int t2 = abs(k - r2);

        if ((lo+1) != i)
        {
          if(res > t1) res = t1;
        }

        if(res>t2) res = t2;
    }
    return res;
    }
};
