class Solution {
public:
    void calc(int x, vector<int>& bit,int op) {
        for (int j = 0; j < 31; ++j) {
            if (x & (1LL << j))
                bit[j]+=op;
        }
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
//         int dp[n][31];
//         memset(dp,0,sizeof(dp));
//         for(int i=0;i<n;i++) {
//             for(int j=0;j<31;j++) {
//                 if(nums[i]&(1<<j)) {
//                     dp[i][j]++;
//                 }
//             }
//             if(i) dp[i][j]+=dp[i-1][j];
//         }
//         for(int i=0;i<n;i++) {
//             int l=i, r=n-1;
//             while(l<=r) {

//             }
//         }
//         return ans;
        int ans=abs(nums[0]-k),val=nums[0],l=0,r=0;
        vector<int> bit(31,0);
        calc(nums[0],bit,1);
        while(r<n) {
            if(val>k or l>r) {
                if(r==n-1) break;
                r++;
                calc(nums[r],bit,1);
            }
            else {
                calc(nums[l],bit,-1);
                l++;
            }
            val=0;
            for(int j=0;j<31;j++) {
                if(bit[j]==r-l+1) {
                    val+=(1LL<<j);
                }
            }
            if(l<=r) {
                ans=min(ans,abs(val-k));
            }
        }
        return ans;
    }
};
